import React, {useEffect, useState} from 'react';
import {BrowserRouter as Router, Switch, Route, useHistory, Redirect} from 'react-router-dom'


import AddJobPage from '../AddJobPage'

import JobsPage from '../JobsPage'

import BlogDetails from '../BlogDetails' 

import ContactPage from '../ContactPage' 
import LoginPage from '../LoginPage' 
import SignUpPage from '../SignUpPage'
import Jobs from "../../components/Jobs";
import { redirect } from "react-router-dom";

const AllRoute = () => {
    const [isLoggedIn, setIsLoggedIn] = useState<boolean>(
        () => localStorage.getItem('token') !== null
    );

    useEffect(() => {
        localStorage.setItem('token', JSON.stringify(isLoggedIn));
    }, [isLoggedIn]);


    const logIn = () => setIsLoggedIn(true);

    // pass this callback to components you want to allow logging out
    // it will update the local state and then get persisted
    const logOut = () => setIsLoggedIn(false);

  return (
    <div className="App">
       <Router>
          <Switch>
              <Route exact path="/">
                  {isLoggedIn ? <Redirect to="/Jobs" /> : <PublicHomePage />}
              </Route>
              <Route exact path='/' component={LoginPage}/>


            <Route path='/AddJob' component={AddJobPage}/>

            <Route path='/Jobs' component={JobsPage}/>


            <Route path='/blog-details' component={BlogDetails}/>



             <Route path='/contact' component={ContactPage}/>
            <Route path='/login' component={LoginPage}/>
            <Route path='/signup' component={SignUpPage}/>

          </Switch>
      </Router>

    </div>
  );
}

export default AllRoute;
