import axios from "axios";
const JOBS_BASE_URL = "http://localhost:8089/SpringMVC/job";

class jobsService {



    addJob(job){
        return axios.post(JOBS_BASE_URL+ '/add', job, {
            headers: {
                'Authorization': `Basic ${localStorage.getItem('token')}`
            },
        });
    }




} export default new jobsService()
