import React, {useState} from 'react'

import pmt1 from '../../images/checkout/img-1.png'
import pmt2 from '../../images/checkout/img-2.png'
import pmt3 from '../../images/checkout/img-3.png'
import pmt4 from '../../images/checkout/img-4.png'

import './style.css'
import jobsService from "../../services/jobsService";
import SimpleReactValidator from "simple-react-validator";
import TextField from "@material-ui/core/TextField";
import {MenuItem, Select, TextareaAutosize} from "@material-ui/core";
import {toast} from "react-toastify";

const AddJob = (props) => {

    const [job, setJob] = useState({
        jobTitle: '',
        jobDescription: '',
        location: '',
        contarctType:'',
        mail:''
    });

    const SubmitHandler = (e) =>{
        e.preventDefault()
        if (validator.allValid()){
            setJob({
                jobTitle: '',
                jobDescription: '',
                location: '',
                contarctType:'',
                mail:''
            });
            validator.hideMessages();
            let theJob = {jobTitle: job.jobTitle, jobDescription: job.jobDescription, location: job.location, contarctType: job.contarctType, mail:job.mail};
            jobsService.addJob(theJob).then(res => {
                toast.success('Job ajoutÃ© avec success ');
                setJob({
                    jobTitle: '',
                    jobDescription: '',
                    location: '',
                    contarctType:'',
                    mail:''
                }   )

            }).catch( toast.error('Your city should be in French Polynesia '))
        }


    }
    const changeHandler = (e) => {
        setJob({...job, [e.target.name]: e.target.value});
        validator.showMessages();
        console.log(e.target.value)

    };
    const [validator] = React.useState(new SimpleReactValidator({
        className: 'errorMessage'
    }));

    return(
        <div className="tp-donation-page-area section-padding">
            <div className="container">
                <div className="row">
                    <div className="col-lg-8 offset-lg-2">
                        <div className="tp-donate-header">
                            <h2>Add Job</h2>
                        </div>
                        <div id="Donations">
                            <form onSubmit={SubmitHandler} action="#">

                                <div className="tp-donations-details">
                                    <h2>Details</h2>
                                    <div className="row">
                                        <div className="col-lg-12 col-12 form-group">
                                            <TextField
                                                className="form-control"
                                                placeholder="Title"
                                                value={job.jobTitle}
                                                variant="outlined"
                                                name="jobTitle"
                                                type="text"
                                                label="Title"
                                                InputLabelProps={{
                                                    shrink: true,
                                                }}
                                                onBlur={(e) => changeHandler(e)}
                                                onChange={(e) => changeHandler(e)}
                                            />

                                        </div>
                                        <div className="col-lg-12 col-12 form-group">
                                            <TextField
                                                className="form-control"
                                                placeholder="Location"
                                                value={job.location}
                                                variant="outlined"
                                                name="location"
                                                type="text"
                                                label="Location"
                                                onBlur={(e) => changeHandler(e)}
                                                onChange={(e) => changeHandler(e)}
                                            />
                                        </div>
                                        <div className="col-lg-12 col-12 form-group">
                                            <TextField
                                                className="form-control"
                                                placeholder="mail"
                                                value={job.mail}
                                                variant="outlined"
                                                name="mail"
                                                type="text"
                                                label="mail"
                                                onBlur={(e) => changeHandler(e)}
                                                onChange={(e) => changeHandler(e)}
                                            />
                                        </div>

                                        <div className="col-lg-12 col-12 form-group">
                                            <Select
                                                className="form-control"
                                                value={job.contarctType}
                                                variant="outlined"
                                                name="contarctType"
                                                type="text"
                                                label="Type"
                                                onBlur={(e) => changeHandler(e)}
                                                onChange={(e) => changeHandler(e)}
                                            >
                                            <MenuItem value="CDI">CDI</MenuItem>
                                            <MenuItem value="CDD">CDD</MenuItem>
                                            <MenuItem value="IntÃ©rim">IntÃ©rim</MenuItem>
                                            <MenuItem value="Stage">IntÃ©Stagerim</MenuItem>
                                            </Select>
                                        </div>
                                        <div className="col-lg-12 col-12 form-group">
                                            <TextareaAutosize
                                                className="form-control"
                                                placeholder="Description"
                                                value={job.jobDescription}
                                                variant="outlined"
                                                name="jobDescription"
                                                type="text"
                                                label="Description"
                                                onBlur={(e) => changeHandler(e)}
                                                onChange={(e) => changeHandler(e)}
                                            />                                        </div>
                                    </div>
                                </div>

                                <div className="submit-area sub-btn">
                                    <button type="submit" className="theme-btn submit-btn">Add</button>
                                </div>
                            </form> 
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default AddJob;
